import React from 'react';

interface Props {
    images: Array<string>;
}

export default class ImageCarousal extends React.Component<Props> {
    images: Array<string>;
    index = 0;

    constructor(props: Props) {
        super(props);
        this.images = props.images;
    }

    moveLeft() {
        this.index -= 1;
        if (this.index < 0) {
            this.index = this.images.length - 1;
        }
    }

    moveRight() {
        this.index += 1;
        if (this.index == this.images.length) {
            this.index = 0;
        }
    }

    render() {
        return (
            <div className='sculpture-photo-container'>
                <img className='sculpture-photo' src={this.images[this.index]} />
                <div className="carousal-controls-container">
                    <a>&#8592;</a>
                    <a>&#8594;</a>
                </div>
            </div>
        );
    }
};
