import React from 'react';

const IMAGE_PATH = '../images/sculptures/';

interface Props {
    key: number;
    forSale: boolean;
    imageName: string;
}

export default class SculptureThumbnail extends React.Component<Props> {
    render() {
        const {key, forSale, imageName} = this.props;

        return (
            <div key={key} className="sculpture-thumbnail">
                <img src={IMAGE_PATH+imageName} />
            </div>
        );
    }
};
