    forSale: boolean;
    imageName: string;
}

export default class SculptureThumbnail extends React.Component<Props> {
    render() {
        const {forSale, imageName} = this.props;
        const imagePath = IMAGE_PATH+imageName;
        console.log(imagePath)
        const style = {
            background: `url(../images/chinatown_nyc.jpg)`,
            width: 100,
            height: 100,
        };


        return (
            <div className="sculpture-thumbnail" style={style}>

            </div>
        );
    }
};
