import React from 'react';
import Header from "./Header";
import Link from 'next/link';

interface Props {
    currentSculptureName: string;
    sculptures: Array<Sculpture>;
}

export default class NavSideBar extends React.Component<Props> {
    render() {
        const { currentSculptureName, sculptures } = this.props;
        const sculptureLinks = sculptures.map(s => (
            <li>
                <Link href="/p/[id]" as={`/p/${s.name}`}>
                    <a>{s.name}</a>
                </Link>
            </li>
        ));

        return (
            <nav role="navigation">
                <div id="menuToggle">
                    {/* Hidden; for using :checked CSS selector. */}
                    <input type="checkbox" id="menuCheckbox" />
                    {/* The hamburger bars. */}
                    <span></span>
                    <span></span>
                    <span></span>

                    <ul id="menu">
                        <li>
                            <Header color={"white"} fontSize="1.5em">Available Sculptures</Header>
                        </li>
                        {sculptureLinks}
                        <li>
                            <Link href="/" as="/">
                                <a>Back to Home</a>
                            </Link>
                        </li>
                    </ul>
                </div>
            </nav>
        );
        // return (
        //     <div className="nav-container">
        //         <Header useSerif={true}>Home</Header>
        //     </div>
        // )
    }
}
